import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleAddTodo = () => {
    if (inputValue.trim() !== '') {
      setTodos([...todos, inputValue]);
      setInputValue('');
    }
  };

  const handleDeleteTodo = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const newTodos = Array.from(todos);
    const [reorderedItem] = newTodos.splice(result.source.index, 1);
    newTodos.splice(result.destination.index, 0, reorderedItem);
    setTodos(newTodos);
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-gray-100 rounded shadow-lg">
      <h1 className="text-2xl mb-4">Tasks</h1>
      <div className="flex">
        <input
          type="text"
          className="flex-1 rounded-l border border-gray-300 p-2"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Add a new todo..."
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded-r"
          onClick={handleAddTodo}
        >
          Add
        </button>
      </div>
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="todos"> {/* Ensure this droppableId matches */}
          {(provided) => (
            <ul className="mt-4" ref={provided.innerRef}>
              {todos.map((todo, index) => (
                <Draggable key={index} draggableId={`todo-${index}`} index={index}>
                  {(provided) => (
                    <li
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="flex justify-between items-center bg-white border border-gray-300 p-2 rounded mb-2"
                    >
                      <span>{todo}</span>
                      <button className="white" onClick={() => handleDeleteTodo(index)}>
                        Delete
                      </button>
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default TodoList;
